package com.orange.womenassist;import androidx.annotation.NonNull;import androidx.appcompat.app.AppCompatActivity;import android.content.Context;import android.content.Intent;import android.content.SharedPreferences;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.ImageView;import android.widget.TextView;import android.widget.Toast;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.android.material.progressindicator.CircularProgressIndicator;import com.google.firebase.firestore.CollectionReference;import com.google.firebase.firestore.DocumentSnapshot;import com.google.firebase.firestore.FirebaseFirestore;import com.orange.womenassist.models.Association;import com.orange.womenassist.models.User;import com.orange.womenassist.utilities.AlertUtility;public class CompteAssociation extends AppCompatActivity {    private User user;    private EditText edtAbreviation, edtNomAssociation, edtTelephone,edtEmail,edtEmplacement;    private static final String USER_CREDENTIAL = "com.orange.womenassist";    private TextView tvNeedHelp, tvAddResponsable;    private Button btnNext;    private ImageView btnBack;    CircularProgressIndicator indicator;    private static final String USER_COLLECTION = "Utilisateurs";    private static final String ASSOCIATION_COLLECTION = "Association";    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_compte_association);        Intent intent = getIntent();        user = (User)intent.getParcelableExtra(USER_COLLECTION);        btnNext = findViewById(R.id.btnNext);        tvNeedHelp = findViewById(R.id.tvNeedHelp);        tvAddResponsable = findViewById(R.id.tvAddResponsable);        edtAbreviation = findViewById(R.id.edtAbreviation);        edtNomAssociation = findViewById(R.id.edtNomAssociation);        edtTelephone = findViewById(R.id.edtTelephone);        edtEmail = findViewById(R.id.edtEmail);        btnBack = findViewById(R.id.btnBack);        edtEmplacement = findViewById(R.id.edtEmplacement);        indicator = findViewById(R.id.indicator);        tvNeedHelp.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(CompteAssociation.this, Aide.class));            }        });        btnBack.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                CompteAssociation.this.onBackPressed();            }        });        tvAddResponsable.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Toast.makeText(CompteAssociation.this, "En cours de d√©veloppement", Toast.LENGTH_LONG).show();            }        });        btnNext.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                indicator.setVisibility(View.VISIBLE);                if((edtAbreviation.getText().toString()).isEmpty() || (edtNomAssociation.getText().toString()).isEmpty()                    || edtEmail.getText().toString().isEmpty() || edtTelephone.getText().toString().isEmpty() || edtEmplacement.getText().toString().isEmpty())                {                    AlertUtility.simpleAlert(CompteAssociation.this, "Erreur","Tous les champs sont obligatoirs.").show();                    indicator.setVisibility(View.INVISIBLE);                    return;                }                Association association = new Association();                association.setNom_association(edtNomAssociation.getText().toString());                association.setAbreviation(edtAbreviation.getText().toString());                association.setEmail(edtEmail.getText().toString());                association.setTelephone(edtTelephone.getText().toString());                association.setEmplacement(edtEmplacement.getText().toString());                Intent intent = new Intent(CompteAssociation.this, CompteAssociation2.class);                intent.putExtra(CompteAssociation.ASSOCIATION_COLLECTION,association);                intent.putExtra(CompteAssociation.USER_COLLECTION,user);                indicator.setVisibility(View.INVISIBLE);                startActivity(intent);                finish();            }        });    }}